	public List<Integer> findSubstring(String s, String[] words) {
		List<Integer> ret = new ArrayList<>();
		int len = s.length();
		int singleLen = words[0].length();
		int targetLen = words.length * singleLen;
		if (len < targetLen) {
			return ret;
		}
		Map<String, Long> wordCountMap = Arrays.stream(words)
				.collect(Collectors.groupingBy(Function.identity(), Collectors.counting()));
		int index = 0;
		while(index + targetLen <= len) {
			Map<String, Long> currentCountMap = new HashMap<>();
			Map<String, List<Integer>> currentIndexMap = new HashMap<>();
			boolean find = true;
			for (int i = 0; i < words.length; i++) {
				int currentIndex = index + i * singleLen;
				String word = s.substring(currentIndex, currentIndex + singleLen);
				if(wordCountMap.containsKey(word)) {
					long expectCount = wordCountMap.get(word);
					Long existingCount = currentCountMap.getOrDefault(word, 0L);
					if(expectCount == existingCount) {
						find = false;
//						List<Integer> list = currentIndexMap.get(word);
//						int offset = list.get(0);
						break;
					} else {
						currentCountMap.put(word, existingCount + 1);
						List<Integer> list = currentIndexMap.getOrDefault(word, new ArrayList<>());
						list.add(currentIndex);
						currentIndexMap.put(word, list);
					}
				} else {
					find = false;
				}
			}
			if(find) {
				ret.add(index);
			}
			index++;
		}
		return ret;
	}

